function setupNewsletterSignup(){$("#submit-signup").click(function(e){e.preventDefault();var s=$("#newsletter-email").val();signUpSubscriber({emailAddress:s})})}function signUpSubscriber(e){$.ajax({url:"/api/v1/newsletter/signup",method:"POST",data:e,beforeSend:signUpSubscriber_beforeSend,success:signUpSubscriber_success,error:signUpSubscriber_error,complete:signUpSubscriber_complete})}function signUpSubscriber_beforeSend(){$("#submit-signup").text("Submitting...")}function signUpSubscriber_success(e,s,n){e&&"success"===e.status?$("#submission-result").empty().append("<span>Success!</span>"):($("#submission-result").empty().append("<span>Error!</span>"),$("#newsletter-email").addClass("error"))}function signUpSubscriber_error(e,s,n){var r=e.responseJSON;$("#submission-result").empty().append("<span>"+r.message+"</span>"),$("#newsletter-email").addClass("error")}function signUpSubscriber_complete(){$("#submit-signup").text("Submit")}$(document).ready(function(){var e=5,s=9,n=120,r=parseInt(60*e/s),t=parseInt(60*e/n),i=r+t,u=$(".counter").FlipClock(i,{clockFace:"Counter"}),c=1;setInterval(function(){c%n==0?u.increment():c%s==0&&u.increment(),c++},1e3),setupNewsletterSignup()});