function setupNewsletterSignup(){$("#submit-signup").click(function(e){e.preventDefault();var s=$("#newsletter-email").val();signUpSubscriber({emailAddress:s})})}function signUpSubscriber(e){$.ajax({url:"/api/v1/newsletter/signup",method:"POST",data:e,beforeSend:signUpSubscriber_beforeSend,success:signUpSubscriber_success,error:signUpSubscriber_error,complete:signUpSubscriber_complete})}function signUpSubscriber_beforeSend(){$("#submit-signup").text("Submitting...")}function signUpSubscriber_success(e,s,r){e&&"success"===e.status?$("#submission-result").empty().append("<span>Success!</span>"):($("#submission-result").empty().append("<span>Error!</span>"),$("#newsletter-email").addClass("error"))}function signUpSubscriber_error(e,s,r){var n=e.responseJSON;$("#submission-result").empty().append("<span>"+n.message+"</span>"),$("#newsletter-email").addClass("error")}function signUpSubscriber_complete(){$("#submit-signup").text("Submit")}var number=0===Math.floor(2*Math.random())?"1":"2";$(".masthead").css("background-image","url(../img/woman"+number+".jpg)"),$(document).ready(function(){var e=5,s=9,r=120,n=parseInt(60*e/s),t=parseInt(60*e/r),i=n+t,u=$(".counter").FlipClock(i,{clockFace:"Counter"}),c=1;setInterval(function(){c%r==0?u.increment():c%s==0&&u.increment(),c++},1e3),setupNewsletterSignup()});